import{_ as e,c as r,o as s,ae as n}from"./chunks/framework.D6wf4lw6.js";const h=JSON.parse('{"title":"高轴 vs. 矮轴","description":"","frontmatter":{},"headers":[],"relativePath":"docs/基础定制/轴体选择.md","filePath":"docs/基础定制/轴体选择.md"}'),l={name:"docs/基础定制/轴体选择.md"};function o(a,t,d,g,i,f){return s(),r("div",null,[...t[0]||(t[0]=[n('<h1 id="高轴-vs-矮轴" tabindex="-1">高轴 vs. 矮轴 <a class="header-anchor" href="#高轴-vs-矮轴" aria-label="Permalink to &quot;高轴 vs. 矮轴&quot;">​</a></h1><p>由于轴座不同，所以高轴和矮轴pcb不能通用!</p><h2 id="" tabindex="-1"><a class="header-anchor" href="#" aria-label="Permalink to &quot;&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">组件</th><th style="text-align:left;">高轴 (常规轴体)</th><th style="text-align:left;">矮轴 (Low-Profile)</th><th style="text-align:left;">影响与分析</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>定位板厚度</strong></td><td style="text-align:left;"><strong>较厚</strong><br>（通常为1.5mm）</td><td style="text-align:left;"><strong>较薄</strong><br>（不超过1.2mm）</td><td style="text-align:left;"><strong>影响</strong>：矮轴体本身的高度大幅降低，为了维持整体的低平外观，定位板也必须做得更薄。<br><strong>分析</strong>：更薄的定位板可能会轻微影响整体的刚性，但在追求“软弹”手感的Gasket结构键盘中，这种差异有时会被利用来增强形变。</td></tr><tr><td style="text-align:left;"><strong>轴座设计</strong></td><td style="text-align:left;"><strong>标准尺寸</strong><br>（Cherry MX规格）</td><td style="text-align:left;"><strong>重新设计，更为紧凑</strong></td><td style="text-align:left;"><strong>影响</strong>：矮轴的引脚（金属针脚和塑料支柱）更短，间距也可能不同，导致其<strong>与高轴不兼容</strong>。<br><strong>分析</strong>：PCB上的轴座（轴体插座）必须为矮轴的特殊引脚布局进行重新设计和布局，其本身的物理尺寸也变得更小、更扁平，以适应更薄的总体空间。</td></tr><tr><td style="text-align:left;"><strong>PCB厚度</strong></td><td style="text-align:left;"><strong>标准厚度</strong><br>（通常为1.6mm）</td><td style="text-align:left;"><strong>基本相同或略微变薄</strong><br>（常见仍为1.6mm）</td><td style="text-align:left;"><strong>影响</strong>：PCB的厚度主要由电路层数和机械强度要求决定，与使用高轴或矮轴关系不大。<br><strong>分析</strong>：虽然整体空间被压缩，但PCB仍需保证基本的电气性能和结构强度，因此其厚度通常是最后才被考虑减薄的环节，变化不大。</td></tr></tbody></table>',4)])])}const x=e(l,[["render",o]]);export{h as __pageData,x as default};
