import{_ as o,c as r,o as n,ae as s}from"./chunks/framework.D6wf4lw6.js";const q=JSON.parse('{"title":"满配内部填充与声学组件全解析","description":"","frontmatter":{},"headers":[],"relativePath":"en-US/深度定制/满配声音包.md","filePath":"en-US/深度定制/满配声音包.md"}'),e={name:"en-US/深度定制/满配声音包.md"};function g(l,t,a,d,i,u){return n(),r("div",null,[...t[0]||(t[0]=[s('<h1 id="满配内部填充与声学组件全解析" tabindex="-1">满配内部填充与声学组件全解析 <a class="header-anchor" href="#满配内部填充与声学组件全解析" aria-label="Permalink to &quot;满配内部填充与声学组件全解析&quot;">​</a></h1><blockquote><p><strong>核心要点</strong>：填充物的终极目的是 <strong>&quot;净化&quot;与&quot;塑造&quot;声音</strong>。它们通过吸收不必要的振动和杂音，并精细调整振动的传递特性，让轴体本身产生的干净、悦耳的基础声音得以清晰地展现，并朝着你喜爱的音色方向演变，从而极大地提升打字的听觉体验。</p></blockquote><h2 id="核心组件对比表" tabindex="-1">核心组件对比表 <a class="header-anchor" href="#核心组件对比表" aria-label="Permalink to &quot;核心组件对比表&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">部件名称</th><th style="text-align:left;">位置</th><th style="text-align:left;">主要作用</th><th style="text-align:left;">常见材质与特性</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>轴上垫</strong></td><td style="text-align:left;">键帽与轴体上盖之间</td><td style="text-align:left;">1. <strong>消除键帽杂音</strong>：填充键帽与轴心柱之间的空隙，消除晃动声。<br>2. <strong>微调触感</strong>：使触底感觉略微更柔和。</td><td style="text-align:left;">薄片状的Poron、硅胶或特氟龙，硬度较高，对声音影响较小。</td></tr><tr><td style="text-align:left;"><strong>夹心棉</strong></td><td style="text-align:left;">定位板与PCB板之间</td><td style="text-align:left;">1. <strong>消除空腔音</strong>：吸收定位板与PCB之间空腔的振动和回声。<br>2. <strong>统一声音</strong>：使各位置按键声音一致。<br>3. <strong>过滤高频噪音</strong>：让音色更纯粹、沉闷。</td><td style="text-align:left;"><strong>PORON</strong>：慢回弹，寿命长，效果最佳。<br><strong>IXPE</strong>：性价比高，效果良好。<br><strong>硅胶</strong>：重量大，吸音效果好，能增重。</td></tr><tr><td style="text-align:left;"><strong>轴下垫</strong></td><td style="text-align:left;">PCB与轴体之间</td><td style="text-align:left;">1. <strong>消除轴体杂音</strong>：吸收轴体内部金属弹片音和触底撞击PCB的高频噪音（&quot;叮&quot;声）。<br>2. <strong>塑造核心音色</strong>：是打造&quot;Hifi&quot;音的关键。<br>3. <strong>软化触底</strong>：使触底感更&quot;肉&quot;。</td><td style="text-align:left;"><strong>PORON/IXPE</strong>：能极致地消除杂音，让轴体声音更干净、集中。</td></tr><tr><td style="text-align:left;"><strong>声优膜</strong></td><td style="text-align:left;">贴附于轴下垫和夹心棉之间</td><td style="text-align:left;">1. <strong>进一步提纯音色</strong>：作为轴下垫的补充，通过不同材质或厚度，<strong>微调</strong>触底音的音色和频率，使其更沉闷或更清脆。<br>2. <strong>改变触底硬度</strong>：提供比轴下垫更细微的触底手感变化。</td><td style="text-align:left;"><strong>PE</strong></td></tr><tr><td style="text-align:left;"><strong>轴座棉</strong></td><td style="text-align:left;">PCB底部，覆盖在轴座之上</td><td style="text-align:left;"><strong>补充吸音</strong>：从底部吸收透过PCB的残余振动和特定频率的噪音。</td><td style="text-align:left;">较薄的PORON或IXPE。注意可能影响轴座安装。</td></tr><tr><td style="text-align:left;"><strong>声优背膜</strong></td><td style="text-align:left;">贴附于轴座棉和底棉之间</td><td style="text-align:left;">1. <strong>消除PCB板颤</strong>：抑制PCB本身因振动产生的细微&quot;嗡&quot;声。<br>2. <strong>补充高频吸收</strong>：从背面进一步过滤通过PCB孔的残余高频噪音。<br>3. <strong>改变PCB振动特性</strong>：使PCB整体更&quot;硬&quot;，从而影响传递到底棉的振动频率。</td><td style="text-align:left;"><strong>PE</strong></td></tr><tr><td style="text-align:left;"><strong>底棉</strong></td><td style="text-align:left;">键盘PE外壳底部，PCB之下</td><td style="text-align:left;">1. <strong>消除最大空腔音</strong>：填充PCB与底壳间的巨大空腔，消除低沉、空洞的回响。<br>2. <strong>增加稳重感</strong>：使键盘敲击时更扎实。</td><td style="text-align:left;"><strong>PORON</strong>：效果最好，寿命长。<br><strong>消音棉/EVA</strong>：成本低，但可能老化。<br><strong>硅胶</strong>：效果极佳，能大幅增重，需定制。</td></tr></tbody></table><h2 id="详细解读与系统级影响" tabindex="-1">详细解读与系统级影响 <a class="header-anchor" href="#详细解读与系统级影响" aria-label="Permalink to &quot;详细解读与系统级影响&quot;">​</a></h2><h3 id="_1-作用层级与声音包概念" tabindex="-1">1. 作用层级与声音包概念 <a class="header-anchor" href="#_1-作用层级与声音包概念" aria-label="Permalink to &quot;1. 作用层级与声音包概念&quot;">​</a></h3><p>可以将这些组件理解为一个协同工作的 <strong>&quot;声音包&quot;</strong> 或 <strong>&quot;消音系统&quot;</strong>：</p><ul><li><strong>基础净化层（消除结构噪音）</strong>：<strong>底棉</strong> 和 <strong>夹心棉</strong>。它们负责处理因键盘物理结构产生的<strong>空腔音</strong>和<strong>回声</strong>，解决了声音&quot;塑料感&quot;和&quot;空洞感&quot;的基础问题。</li><li><strong>核心塑造层（消除声源噪音）</strong>：<strong>轴下垫</strong> 和 <strong>声优膜</strong>。它们直接作用于声音的源头——轴体，负责消除轴体自身的<strong>金属杂音</strong>，并通过材质和厚度来<strong>主动塑造</strong>核心的按键音色，是&quot;麻将音&quot;、&quot;石子音&quot;的灵魂所在。</li><li><strong>微调优化层（精细抑振）</strong>：<strong>声优背膜</strong>、<strong>轴座棉</strong> 和 <strong>轴上垫</strong>。它们对整体体验进行精细调整，解决PCB板振、轴座杂音和键帽晃动等细节问题。</li></ul><h3 id="_2-重要注意事项" tabindex="-1">2. 重要注意事项 <a class="header-anchor" href="#_2-重要注意事项" aria-label="Permalink to &quot;2. 重要注意事项&quot;">​</a></h3><ul><li><strong>过犹不及</strong>：填充过厚或过硬的棉会<strong>增加键盘内部的压力</strong>，可能导致PCB弯曲、轴体引脚接触不良、甚至使Gasket结构的&quot;软弹&quot;手感消失。务必选择合适厚度的填充物。</li><li><strong>从上到下顺序</strong>：通常的安装顺序是：<strong>轴上垫 → 定位板 → 夹心棉 → 声优膜 → 轴下垫 → PCB电路板 → 轴座棉 → 声优背膜 → 底棉</strong>。</li><li><strong>声音是系统工程</strong>：填充物只是&quot;净化&quot;与&quot;塑造&quot;声音，而键盘的最终音色是由 <strong>轴体（核心声源） + 定位板（调音板） + 填充物（消音系统） + 键帽（共鸣腔） + 结构（传播路径）</strong> 共同决定的。</li></ul>',10)])])}const x=o(e,[["render",g]]);export{q as __pageData,x as default};
